var fs = require('fs'),ls
    conf = require('../conf/config.js');

module.exports = function(caller) {
  var page = caller.split('views/').pop().replace('.html', '')
  if (!conf['static-optimized']) {
    var links = fs.readFileSync(require('../app').path+'/'+conf['message-path']+'views/'+page+'.stat').toString();
    return links;
  }
  else {
    var js = '<script type="text/javascript" src="'+[conf['static-path'], conf['build-number'], 'js', page].join('/')+'.js"></script>'
    var css = '<link type="text/css" href="'+[conf['static-path'], conf['build-number'], 'css', page].join('/')+'.css" rel="stylesheet"/>'
    return js+css;
  }
}